// Variables
var testID = '001',
    testDays = 8,
    randomNumber = {{Random Number}},
    testVariant = readCookie(testID),
    previewUrl = true,
    variants = {
      1: {
        execute: function() {
          //
        }
      },
      2: {
        execute: function() {
          //
        }
      }
    };

// Helpers
function setCookie(testID, variantID, testDays) {
  var date = new Date();
  date.setTime(date.getTime() + (testDays * 86400000));
  var expires = '; expires=' + date.toGMTString();
  document.cookie = 'tnw-' + testID + '=' + variantID + expires + '; path=/';
}

function readCookie(testID) {
  var nameEQ = 'tnw-' + testID + '=',
      ca = document.cookie.split(';');

  for (var i = 0; i < ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) == ' ') c = c.substring(1, c.length);
    if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
  }
  return null;
}

function checkCookie(name) {
  ca = document.cookie.split(';')

  for (var i = 0; i < ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) === ' ') c = c.substring(1, c.length);
    if (c.indexOf(name) > -1) return true;
  }
  return false;
}

function sendDimension(variant) {
  if (checkCookie('_ga')) ga("set",'dimensionXX','tnw-' + testID + '-' + variant);
}

function getParameterByName(name) {
  name = name.replace(/[\[]/, "\\\[").replace(/[\]]/, "\\\]");
  var regexS = "[\\?&]" + name + "=([^&#]*)";
  var regex = new RegExp(regexS);
  var results = regex.exec(window.location.search);
  if(results == null) {
    return "";
  } else {
    return decodeURIComponent(results[1].replace(/\+/g, " "));
  }
}

function control(testVariant) {
  setCookie(testID, testVariant, testDays);
  sendDimension(testVariant);
}

function variant(variantID) {
  control(variantID);
  if (variantID > 0) variants[variantID].execute();
}

// Check variants v.s. cookies
var variantsRandom = Math.round(2147483647 / (Object.keys(variants).length + 1));

if(previewUrl && getParameterByName('previewUrl') != "") {
  variant(getParameterByName('previewUrl'));
} else if (testVariant) {
  variant(testVariant);
} else if (!checkCookie('tnw-')) {
  var chosen = 0;
  for (var j = 0; j <= 2147483647; j+= variantsRandom) {
    if (randomNumber <= variantsRandom) {
      variant(chosen);
      break;
    } else if (randomNumber >= j && randomNumber < (j + variantsRandom)) {
      variant(chosen);
      break;
    }
    chosen++;
  }
}

function() {
  try {
    var trackers = ga.getAll();
    var i, len;
    for (i = 0, len = trackers.length; i < len; i += 1) {
      if (trackers[i].get('trackingId') === {{Google Analytics - Tracking ID Lookup Table}}) {
        return trackers[i].get('clientId');
      }
    }
  } catch(e) {}  
  return 'false';
}

function() {
    var now = new Date();
    var tzo = -now.getTimezoneOffset();
    var dif = tzo >= 0 ? '+' : '-';
    var pad = function(num) {
        var norm = Math.abs(Math.floor(num));
        return (norm < 10 ? '0' : '') + norm;
    };
    return now.getFullYear() 
        + '-' + pad(now.getMonth()+1)
        + '-' + pad(now.getDate())
        + 'T' + pad(now.getHours())
        + ':' + pad(now.getMinutes()) 
        + ':' + pad(now.getSeconds())
        + '.' + pad(now.getMilliseconds())
        + dif + pad(tzo / 60) 
        + ':' + pad(tzo % 60);
}

function() {
  switch ({{JS - Visibility Prefix}}) {
    case '':
      return document['hidden'];
    case 'moz':
      return document['mozHidden'];
    case 'o':
      return document['oHidden'];
    case 'webkit':
      return document['webkitHidden'];
    default:
      return;
  }
}

function() {
  var prefixes = ['moz', 'ms', 'o', 'webkit'];

  if ('hidden' in document) {
    return '';
  }

  for (var i = 0; i < prefixes.length; i++) {
    var testPrefix = prefixes[i] + 'Hidden';
    if (testPrefix in document) {
      return prefixes[i];
    }
  }

  return;
}
